mapper.update:
update fund_history
set fi_id = #{fiId,jdbcType=VARCHAR},
  time = #{time,jdbcType=DATE},
  netvalue = #{netvalue,jdbcType=DOUBLE},
  rate = #{rate,jdbcType=DOUBLE}
where id = #{id,jdbcType=INTEGER}

mapper.select:
  	select * from fund_history


	  		and time >= #{date1}


  			and time <= #{date2}


  			and fi_id = #{str1}


  			and fi_id in (select id from fund_info where name like '%${str2}%')



  		order by ${sort} ${order}


  		order by time desc

  	limit ${start},${rows}

  	select count(*) from fund_history


	  		and time >= #{date1}


  			and time <= #{date2}


  			and fi_id = #{str1}


  			and fi_id in (select id from fund_info where name like '%${str2}%')
