mapper.select:
select 

from users
where usernum = #{num,jdbcType=VARCHAR} and userpass=#{pass,jdbcType=VARCHAR}

  	select 






  	from users


	  		and create_time >= #{date1}


  			and create_time <= #{date2}


  			and usernum like '${str1}%'


  			and name like '${str2}%'



  		order by ${sort} ${order}

  	limit ${start},${rows}

  	select count(id) from users


	  		and create_time >= #{date1}


  			and create_time <= #{date2}


